
"""
求第k大的subarray sum
n^2 * log(n^2)

sum(left-th subarray sum + ... + right-th subarray sum)
kth subarray sum = S

countSmallerOrEqual(S) < k -> left = S + 1
countSmallerOrEqual(S) >= k -> right = S


"""

class Solution:
    def rangeSum(self, nums, n: int, left: int, right: int) -> int:
        preSum = []
        mod = 10 ** 9 + 7
        for i in range(n):
            summ = 0
            for j in range(i, n):
                summ += nums[j]
                preSum.append(summ)

        preSum.sort()
        return sum(preSum[left - 1: right]) % mod


"""
求第k大的subarray sum
n^2 * log(n^2)

sum(left-th subarray sum + ... + right-th subarray sum)
kth subarray sum = S

countSmallerOrEqual(S) < k -> left = S + 1
countSmallerOrEqual(S) >= k -> right = S


"""


class Solution2:
    def rangeSum(self, nums, n: int, left: int, right: int) -> int:
        self.nums = nums
        self.sum = sum(nums)
        res = 0
        mod = 10 ** 9 + 7
        for k in range(left, right + 1):
            res = (res + self.kthSum(k)) % mod
        return res

    def kthSum(self, k):
        left, right = 1, self.sum
        while left < right:
            print(left, right)
            mid = left + (right - left) // 2
            if self.countSmallerOrEqual(mid) < k:
                left = mid + 1
            else:
                right = mid
        return left

    def countSmallerOrEqual(self, s):
        j = 0
        count = 0
        summ = 0
        for i in range(len(self.nums)):
            while j < len(self.nums) and summ + self.nums[j] <= s:
                summ += self.nums[j]
                j += 1
            count += j - i
            summ -= self.nums[i]
        return count




